import { createRouter, createWebHistory } from 'vue-router';
import { useAuthStore } from '@/store/auth';

import HomeView from '../views/HomeView.vue';
import LoginView from '../views/LoginView.vue';
import RegisterView from '../views/RegisterView.vue';
import ProductCreate from '../views/ProductCreate.vue';
import ProductDetail from '../views/ProductDetail.vue';

// ===== 定义路由规则 =====
const routes = [
    {
        path: '/',
        name: 'home',
        component: HomeView,
        meta: { requiresAuth: true }, // 需要登录才能访问
    },
    {
        path: '/login',
        name: 'login',
        component: LoginView,
        meta: { requiresAuth: false }, // 登录页不需要权限
    },
    {
        path: '/register',
        name: 'register',
        component: RegisterView,
        meta: { requiresAuth: false },
    },
    {
        path: '/products/create',
        name: 'product-create',
        component: ProductCreate,
        meta: { requiresAuth: true },
    },
    {
        path: '/products/:id',
        name: 'product-detail',
        component: ProductDetail,
    },
    {
        path: '/:pathMatch(.*)*',
        redirect: '/', // 未匹配的路径全部重定向到首页
    },
];

// ===== 创建路由实例 =====
const router = createRouter({
    history: createWebHistory(process.env.BASE_URL),
    routes,
});

// ===== 全局路由守卫 =====
router.beforeEach((to, from, next) => {
    const authStore = useAuthStore();

    // 如果目标路由需要认证但用户未登录 → 跳转到登录页
    if (to.meta.requiresAuth && !authStore.isLoggedIn) {
        next('/login');
    }
    // 如果用户已登录还去 login/register → 跳转到首页
    else if ((to.path === '/login' || to.path === '/register') && authStore.isLoggedIn) {
        next('/');
    }
    // 否则正常进入
    else {
        next();
    }
});

export default router;

import { createRouter, createWebHistory } from 'vue-router';
import { useAuthStore } from '@/store/auth';

import HomeView from '../views/HomeView.vue';
import LoginView from '../views/LoginView.vue';
import RegisterView from '../views/RegisterView.vue';
import ProductCreate from '../views/ProductCreate.vue';
import ProductDetail from '../views/ProductDetail.vue';
import AdminUsers from '../views/AdminUsers.vue';

// ===== 定义路由规则 =====
const routes = [
    {
        path: '/',
        name: 'home',
        component: HomeView,
        meta: { requiresAuth: true }, // 需要登录才能访问
    },
    {
        path: '/login',
        name: 'login',
        component: LoginView,
        meta: { requiresAuth: false }, // 登录页不需要权限
    },
    {
        path: '/register',
        name: 'register',
        component: RegisterView,
        meta: { requiresAuth: false },
    },
    {
        path: '/products/create',
        name: 'product-create',
        component: ProductCreate,
        meta: { requiresAuth: true },
    },
    {
        path: '/products/:id',
        name: 'product-detail',
        component: ProductDetail,
    },
    {
        path: '/admin/users',
        name: 'admin-users',
        component: AdminUsers,
        meta: { requiresAuth: true, requiresAdmin: true },
    },
    {
        path: '/:pathMatch(.*)*',
        redirect: '/', // 未匹配的路径全部重定向到首页
    },
];

// ===== 创建路由实例 =====
const router = createRouter({
    history: createWebHistory(process.env.BASE_URL),
    routes,
});

// ===== 全局路由守卫 =====
router.beforeEach((to, from, next) => {
    const authStore = useAuthStore();

    // 需要登录
    if (to.meta.requiresAuth && !authStore.isLoggedIn) {
        return next('/login');
    }

    // 已登录时禁止访问登录/注册
    if ((to.path === '/login' || to.path === '/register') && authStore.isLoggedIn) {
        return next('/');
    }

    // 需要管理员权限
    if (to.meta.requiresAdmin) {
        const role = authStore.user?.role;
        if (role !== 'ROLE_ADMIN') {
            return next('/');
        }
    }

    return next();
});

export default router;

package lut.cn.c2cplatform.controller;

import lut.cn.c2cplatform.entity.User;
import lut.cn.c2cplatform.mapper.UserMapper;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.web.bind.annotation.*;
import java.util.List;

@RestController
@RequestMapping("/api/admin")
public class AdminController {
    @Autowired
    private UserMapper userMapper;

    @GetMapping("/users")
    @PreAuthorize("hasRole('ADMIN')")
    public ResponseEntity<List<User>> getAllUsers() {
        return ResponseEntity.ok(userMapper.selectAll());
    }
}

package lut.cn.c2cplatform.controller;

import lut.cn.c2cplatform.entity.User;
import lut.cn.c2cplatform.mapper.UserMapper;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.ResponseEntity;
import org.springframework.security.access.prepost.PreAuthorize;
import org.springframework.web.bind.annotation.*;
import java.util.List;

@RestController
@RequestMapping("/api/admin")
public class AdminController {
    @Autowired
    private UserMapper userMapper;

    @GetMapping("/users")
    @PreAuthorize("hasRole('ADMIN')")
    public ResponseEntity<List<User>> getAllUsers() {
        // 返回包含角色信息的用户
        return ResponseEntity.ok(userMapper.selectAllWithRoles());
    }
}

package lut.cn.c2cplatform.mapper;

import lut.cn.c2cplatform.entity.User;
import org.apache.ibatis.annotations.Mapper;
import org.apache.ibatis.annotations.Param;
import java.util.List;

@Mapper
public interface UserMapper {
    User selectById(@Param("id") Long id);
    User selectByUsername(@Param("username") String username);
    // 加载包含角色的用户
    User selectByUsernameWithRoles(@Param("username") String username);
    List<User> selectAll();
    int insert(User user);
    int update(User user);
    int deleteById(@Param("id") Long id);
}

package lut.cn.c2cplatform.mapper;

import lut.cn.c2cplatform.entity.User;
import org.apache.ibatis.annotations.Mapper;
import org.apache.ibatis.annotations.Param;
import java.util.List;

@Mapper
public interface UserMapper {
    User selectById(@Param("id") Long id);
    User selectByUsername(@Param("username") String username);
    // 加载包含角色的用户
    User selectByUsernameWithRoles(@Param("username") String username);
    // 加载所有用户（包含角色）
    List<User> selectAllWithRoles();
    List<User> selectAll();
    int insert(User user);
    int update(User user);
    int deleteById(@Param("id") Long id);
}
