mapper.update:
UPDATE chat_message
SET is_read = true
WHERE sender_id = #{senderId}
  AND recipient_id = #{recipientId}
  AND is_read = false

mapper.select:
SELECT DISTINCT u.username
FROM (
    SELECT sender_id as user_id FROM chat_message WHERE recipient_id = #{userId}
    UNION
    SELECT recipient_id as user_id FROM chat_message WHERE sender_id = #{userId}
) as conversations
JOIN users u ON u.id = conversations.user_id
WHERE u.id != #{userId}
ORDER BY u.username

SELECT COUNT(*)
FROM chat_message
WHERE recipient_id = #{recipientId}
  AND sender_id = #{senderId}
  AND is_read = false
