mapper.select:
SELECT * FROM pms_product
WHERE status = 1

    AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))


    AND price >= #{minPrice}


    AND price <= #{maxPrice}


    AND condition_level = #{conditionLevel}


    AND location LIKE CONCAT('%', #{location}, '%')


    AND FIND_IN_SET(category, #{category}) > 0

ORDER BY created_at DESC

mapper.update:
UPDATE pms_product
SET user_id=#{userId}, name=#{name}, description=#{description}, price=#{price}, stock=#{stock}, condition_level=#{conditionLevel}, location=#{location}, category=#{category}, status=#{status}, updated_at=#{updatedAt}
WHERE id=#{id}

mapper.select:
SELECT * FROM pms_product WHERE id IN
#{id}
AND status = 1
